    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        
        
        if(
            core.Validator.validateAddClientForm(
                    txtIdentidad.getText(),
                    txtNombre.getText(),
                    txtApellido.getText(),
                    txtTelefono.getText()
            )
        ){
            if(Main.jomaHotel.searchIdClient(txtIdentidad.getText()) == false){
                Main.jomaHotel.addClient(
                    Main.jomaHotel.lastIndexClient(),
                    txtIdentidad.getText(),
                    txtNombre.getText(),
                    txtApellido.getText(),
                    txtTelefono.getText()
                );
                JOptionPane.showMessageDialog(null, "Se ah Guardado al cliente exitosamente");
                 try {
                this.setClosed(true);
                } catch (Exception ex) {
                    System.err.println("Closing Exception");
                }
            }else{
                JOptionPane.showMessageDialog(null, "Error: Este cliente ya existe");
            }
        
        }else{
            JOptionPane.showMessageDialog(null, "Error: No se an llenado correctamente todos los campos");
        }
    }//GEN-LAST:event_btnGuardarActionPerformed

